pragma solidity ^0.4.21;

import "zeppelin-solidity/contracts/ownership/Ownable.sol";

/**
 * @title ERC20Basic
 * @dev Simpler version of ERC20 interface
 */
interface ERC20Basic {
    function balanceOf(address who) public view returns (uint256);
    function transfer(address to, uint256 value) public returns (bool);
}

/**
* @title ERC20 Hot Wallet
*/
contract Erc20HotWallet is Ownable {
    /**
    * The contract constructor
    */
    function Erc20HotWallet(address _owner) public {
        owner = _owner;
    }

    /**
    * @dev withdraw  all available amount of a ERC20 token to a specified address
    * @param to The address to transfer to.
    */
    function withdrawAllTokens(address to, ERC20Basic token) public onlyOwner {
        uint256 balance = token.balanceOf(this);
        require(token.transfer(to, balance));
    }
}
